(18, 0, 11, 'lanewechat', 7, '话说那个被动回复图文消息我按照文档构建好了  直接返回无法服务了  求解\r\n public static function text(&amp;$request){\r\n        switch ($request[''content'']) {\r\n            case ''每日一句'':\r\n                //$content = &quot;每日一句马上开发OK，不急不急哈。&quot;;\r\n                $tuwenList = array();\r\n\r\n                $tuwenList[] = array(''title''=&gt;''这是每日一句的标题哦~~~~'', ''description''=&gt;'''', ''pic_url''=&gt;'''', ''url''=&gt;'''');\r\n\r\n                $tuwenList[] = array(''title''=&gt;''哇，内容。'', ''description''=&gt;'''', ''pic_url''=&gt;'''', ''url''=&gt;'''');\r\n\r\n                $itemList = array();\r\n\r\n                       foreach($tuwenList as $tuwen){\r\n\r\n                            $itemList[] = ResponsePassive::newsItem($tuwen[''title''], $tuwen[''description''], $tuwen[''pic_url''], $tuwen[''url'']);\r\n\r\n                        }\r\n                return  ResponsePassive::news($fromusername, $tousername, $itemList);\r\n\r\n                break;            \r\n\r\n            default:\r\n\r\n                $content = &quot;哈哈&quot;;\r\n\r\n                return ResponsePassive::text($request[''fromusername''], $request[''tousername''], $content);\r\n\r\n                break;\r\n        }\r\n\r\n}', '123', '947703573@qq.com', '', 1421779959, 0, 0),
